import { useState } from 'react'

const SquareComp = ({cellValue, handleOnClick}) => {
  return <button className="square" onClick={handleOnClick}>{cellValue}</button>;
}

export default function Board() {

  const [cells, setCells] = useState(Array(9).fill(' '));

  const handleClick = () => {
    const newCells = cells.splice();
    newCells[5] = 'X';
    setCells(newCells);
    console.log("Handling Click");
  }

  return <>
    <div className='board-row'>
      <SquareComp cellValue={cells[0]} handleOnClick={handleClick}/>
      <SquareComp cellValue={cells[1]} handleOnClick={handleClick}/>
      <SquareComp cellValue={cells[2]} handleOnClick={handleClick}/>
    </div>
    <div className='board-row'>
      <SquareComp cellValue={cells[3]} handleOnClick={handleClick}/>
      <SquareComp cellValue={cells[4]} handleOnClick={handleClick}/>
      <SquareComp cellValue={cells[5]} handleOnClick={handleClick}/>
    </div>
    <div className='board-row'>
      <SquareComp cellValue={cells[6]} handleOnClick={handleClick}/>
      <SquareComp cellValue={cells[7]} handleOnClick={handleClick}/>
      <SquareComp cellValue={cells[8]} handleOnClick={handleClick}/>
    </div>
  </>;
}
