import { useState } from 'react'

const SquareComp = ({cellValue, handleOnClick}) => {
  return <button className="square" onClick={handleOnClick}>{cellValue}</button>;
}

export default function Board() {

  const [turn, setTurn] = useState('X');
  const [cells, setCells] = useState(Array(9).fill(' '));

  let statusText = "Game is running"
  const winner = calculateWinner(cells);
  if(winner != ' ') {
    console.log("renderingWin")
    statusText = "Winner: " + winner;
  }
  else {
    console.log("renderingNone")
    statusText = "Winner: " + winner;
  }

  const handleClick = (i) => {
    if(cells[i] !== ' ')
      return;
    const newCells = cells.slice();
    newCells[i] = turn;
    setCells(newCells);
    if(turn == 'X')
      setTurn('O');
    else
      setTurn('X');  
  }

  return <>
    <div className='status'>
      {statusText}
    </div>
    <div className='board-row'>
      <SquareComp cellValue={cells[0]} handleOnClick={() => handleClick(0)}/>
      <SquareComp cellValue={cells[1]} handleOnClick={() => handleClick(1)}/>
      <SquareComp cellValue={cells[2]} handleOnClick={() => handleClick(2)}/>
    </div>
    <div className='board-row'>
      <SquareComp cellValue={cells[3]} handleOnClick={() => handleClick(3)}/>
      <SquareComp cellValue={cells[4]} handleOnClick={() => handleClick(4)}/>
      <SquareComp cellValue={cells[5]} handleOnClick={() => handleClick(5)}/>
    </div>
    <div className='board-row'>
      <SquareComp cellValue={cells[6]} handleOnClick={() => handleClick(6)}/>
      <SquareComp cellValue={cells[7]} handleOnClick={() => handleClick(7)}/>
      <SquareComp cellValue={cells[8]} handleOnClick={() => handleClick(8)}/>
    </div>
  </>;
}

function calculateWinner(cells) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (cells[a] && cells[a] === cells[b] && cells[a] === cells[c]) {
      return cells[a];
    }
  }
  return ' ';
}
