import SquareComp, { Square } from './SquareComp'
import { useState } from 'react'

export default function Board() {

  const [cells, setCells] = useState(Array(9).fill(' '));

  const handleClick = () => {
    newCells = cells.splice();
    newCells[5] = 'X';
    setCells(newCells);
    console.log("Handling Click");
  }

  return <>
    <div className='board-row'>
      <SquareComp cellValue={cells[0]} handleOnClick={handleClick}/>
      <SquareComp />
